import React from 'react'
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import { useState } from 'react';
import List from './List'
import '../node_modules/bootstrap/dist/css/bootstrap.min.css'


const App = () => {
    const [cinput, finput] = useState(" ")
    const [cin, upcin] = useState([])
    const additem = (event) => {
        finput(event.target.value)
    }

    const listitem = () => {
        upcin((prevalue) => {
            return ([...prevalue, cinput])
        })
        finput(" ")
    }
    return (
        <>
            <div className="main_div">
                <div className="cntr_div">
                    <h1>TO DO LIST</h1>
                    <input type="text" placeholder="Add a Item" onChange={additem} value={cinput} />
                    <Button onClick={listitem} className="btn1">
                        <AddIcon />
                    </Button><br />

                    <button className="btn btn-sucess">submit</button>
                    <form>
                        <div class="mb-3">
                            <label for="exampleInputEmail1" class="form-label">Email address</label>
                            <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
                            <div id="emailHelp" class="form-text">We'll never share your email with anyone else.</div>
                        </div>
                        <div class="mb-3">
                            <label for="exampleInputPassword1" class="form-label">Password</label>
                            <input type="password" class="form-control" id="exampleInputPassword1" />
                        </div>
                        <div class="mb-3 form-check">
                            <input type="checkbox" class="form-check-input" id="exampleCheck1" />
                            <label class="form-check-label" for="exampleCheck1">Check me out</label>
                        </div>
                        <button type="submit" class="btn btn-primary">Submit</button>
                    </form>
                    <ol>
                        {cin.map((val, index) => {
                            return (<List key={index} text={val} />)

                        })}
                    </ol>
                </div>
            </div>

        </>
    )
}
export default App
